
window.onload = function () {

	var desplegarLista=function(event)
	{
		event.stopPropagation();
		
		if ($(this).parent('div').find("> ul").css('height')!='0px')
		{
			$('.listasDelPieResponsive').css('height','0px');
		}
		else
		{
			$('.listasDelPieResponsive').css('height','0px');
			$(this).parent('div').find("> ul").css('height','120px');
		}
	};

	$(window).click(function() {
		$('.listasDelPieResponsive').css('height','0px');
	});

	$('.divDeListaDelPieResponsive').click(function(event) {
		event.stopPropagation();
	});

	$('.pieTituloListaResponsive').click(desplegarLista);


	if (window.matchMedia('(max-width: 767px)').matches)
	{
		$('.divDeListaDelPie').removeClass('col-sm-3');
		$('.divDeListaDelPie').addClass('col-sm-12');
		$('.divDeListaDelPie').addClass('divDeListaDelPieResponsive');
		$('.divDeListaDelPieResponsive').removeClass('divDeListaDelPie');
		$('.pieTituloLista').attr('role','button');
		$('.pieTituloLista').attr('aria-haspopup','true');
		$('.pieTituloLista').attr('aria-expanded','false');
		$('.pieTituloLista').addClass('pieTituloListaResponsive');
		$('.pieTituloListaResponsive').removeClass('pieTituloLista');
		$('.listasDelPie').css('height','0px');
		$('.listasDelPieResponsive').css('height','0px');
		$('.listasDelPie').addClass('listasDelPieResponsive');
		$('.listasDelPieResponsive').removeClass('listasDelPie');
		
	}
	else
	{
		$('.divDeListaDelPieResponsive').addClass('divDeListaDelPie');
		$('.divDeListaDelPie').removeClass('divDeListaDelPieResponsive');
		$('.divDeListaDelPie').addClass('col-sm-3');
		$('.divDeListaDelPie').removeClass('col-sm-12');
		$('.pieTituloListaResponsive').addClass('pieTituloLista');
		$('.pieTituloLista').removeClass('pieTituloListaResponsive');
		$('.pieTituloLista').removeAttr('role');
		$('.pieTituloLista').removeAttr('aria-haspopup');
		$('.pieTituloLista').removeAttr('aria-expanded');
		$('.listasDelPieResponsive').addClass('listasDelPie');
		$('.listasDelPie').removeClass('listasDelPieResponsive');
		$('.listasDelPie').css('height','100%');
		$('.listasDelPieResponsive').css('height','100%');
	}



	


};




