var imagenesACarousel=function(event){

		if (window.matchMedia('(max-width: 450px)').matches)
		{
			$('.carouselIndicadores').css('display','inline');
			$('.carouselFlechas').css('display','inline');
			$('.carouseles').addClass('carousel');
			$('.carouseles').addClass('slide');
			$('li').removeClass('active');
			$('.liActivo').addClass('active');
			$('.imagenesALaDer').addClass('carousel-inner');
			$('.imagenesALaIzq').addClass('carousel-inner');
			$('.imagenesAlCentro').addClass('carousel-inner');
			$('.imagenesALaDer').addClass('imagenesEnCarouselDer');
			$('.imagenesALaIzq').addClass('imagenesEnCarouselIzq');
			$('.imagenesAlCentro').addClass('imagenesEnCarouselCentro');
			$('.imagenesEnCarouselDer').removeClass('imagenesALaDer');
			$('.imagenesEnCarouselIzq').removeClass('imagenesALaIzq');
			$('.imagenesEnCarouselCentro').removeClass('imagenesAlCentro');
			$('.itemnes').addClass('item');
			$('.itemnes').removeClass('col-xs-3');
			$('.itemnes').removeClass('active');
			$('.item-activo').addClass('active');
			$('.imagenDentroDelCarousel').css('width','auto')
			$('.imagenDentroDelCarousel').css('height','100%')

		}
		else
		{
			$('.carouselIndicadores').css('display','none');
			$('.carouselFlechas').css('display','none');
			$('.carouseles').removeClass('carousel');
			$('.carouseles').removeClass('slide');
			$('li').removeClass('active');
			$('.imagenesEnCarouselDer').addClass('imagenesALaDer');
			$('.imagenesEnCarouselIzq').addClass('imagenesALaIzq');
			$('.imagenesEnCarouselCentro').addClass('imagenesAlCentro');
			$('.imagenesALaDer').removeClass('imagenesEnCarouselDer');
			$('.imagenesALaIzq').removeClass('imagenesEnCarouselIzq');
			$('.imagenesAlCentro').removeClass('imagenesEnCarouselCentro');
			$('.imagenesALaDer').removeClass('carousel-inner');
			$('.imagenesALaIzq').removeClass('carousel-inner');
			$('.imagenesAlCentro').removeClass('carousel-inner');
			$('.itemnes').removeClass('item');
			$('.item-activo').removeClass('active');
			$('.itemnes').removeClass('item');
			$('.itemnes').removeClass('active');
			$('.itemnes').addClass('col-xs-3');
			$('.imagenDentroDelCarousel').css('width','100%')
			$('.imagenDentroDelCarousel').css('height','auto')

		}
};

window.addEventListener('load', imagenesACarousel);
$( window ).resize(imagenesACarousel);