var ordenarcolumnas4=function(event){
	if (window.matchMedia('(max-width: 767px)').matches)
	{
		if (window.matchMedia('(max-width: 450px)').matches)
		{
			$('.nuestrosServiciosBloque').removeClass('col-xs-3');
			$('.nuestrosServiciosBloque').removeClass('col-xs-6');
			$('.nuestrosServiciosBloque').addClass('col-xs-12');
		}
		else
		{
			$('.nuestrosServiciosBloque').removeClass('col-xs-3');
			$('.nuestrosServiciosBloque').addClass('col-xs-6');
			$('.nuestrosServiciosBloque').removeClass('col-xs-12');
		}
	}
	else
	{
		$('.nuestrosServiciosBloque').addClass('col-xs-3');
		$('.nuestrosServiciosBloque').removeClass('col-xs-6');
		$('.nuestrosServiciosBloque').removeClass('col-xs-12');
	}
};

window.addEventListener('load', ordenarcolumnas4);
$( window ).resize(ordenarcolumnas4);
